import React, { createContext, useState, useEffect } from 'react';

const VoteContext = createContext();

const VoteProvider = ({ children }) => {
  const [voteStatus, setVoteStatus] = useState('tidak');
  const [timer, setTimer] = useState(0);

  useEffect(() => {
    const interval = setInterval(() => {
      setTimer((prevTimer) => prevTimer + 1);
    }, 1000);

    return () => {
      clearInterval(interval);
    };
  }, []);

  const handleVote = (status) => {
    setVoteStatus(status);
  };

  const handleAutoVote = () => {
    if (timer >= 10 && voteStatus === 'tidak') {
      setVoteStatus('masuk');
    }
  };

  return (
    <VoteContext.Provider value={{ voteStatus, handleVote, handleAutoVote }}>
      {children}
    </VoteContext.Provider>
  );
};

export { VoteContext, VoteProvider };
