import React, { useState, useEffect } from 'react';

const Timer = () => {
  const [time, setTime] = useState({ seconds: 0, milliseconds: 0 });
  const [isActive, setIsActive] = useState(false);
  const [isStopped, setIsStopped] = useState(false);

  useEffect(() => {
    let interval = null;

    if (isActive) {
      interval = setInterval(() => {
        setTime((prevTime) => {
          if (prevTime.milliseconds > 0) {
            return {
              seconds: prevTime.seconds,
              milliseconds: prevTime.milliseconds - 1
            };
          } else if (prevTime.seconds > 0) {
            return {
              seconds: prevTime.seconds - 1,
              milliseconds: 999
            };
          } else {
            clearInterval(interval);
            setIsActive(false);
            setIsStopped(true);
            return prevTime;
          }
        });
      }, 1);
    } else if (!isActive && (time.seconds !== 0 || time.milliseconds !== 0)) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isActive, time]);

  const handleStart = () => {
    setIsActive(true);
    setIsStopped(false);
  };

  const handlePause = () => {
    setIsActive(false);
  };

  const handleStop = () => {
    setIsActive(false);
    setIsStopped(true);
    setTime({ seconds: 0, milliseconds: 0 });
  };

  const handleReset = () => {
    setTime({ seconds: 0, milliseconds: 0 });
    setIsStopped(false);
  };

  const formatTime = (value) => {
    return value < 10 ? `0${value}` : value;
  };

  return (
    <div>
      <h1>
        Countdown Timer: {formatTime(time.seconds)}s {formatTime(time.milliseconds)}ms
      </h1>
      <div>
        {!isActive && time.seconds === 0 && time.milliseconds === 0 && (
          <button onClick={handleStart}>Mulai</button>
        )}
        {isActive && (
          <button onClick={handlePause}>Jeda</button>
        )}
        {!isActive && (time.seconds !== 0 || time.milliseconds !== 0) && !isStopped && (
          <button onClick={handleStop}>Henti</button>
        )}
        {!isActive && (time.seconds !== 0 || time.milliseconds !== 0) && isStopped && (
          <div>
            <button onClick={handleReset}>Ulang</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Timer;
