import React, { useState, useRef } from 'react';

const Timer = () => {
  const [time, setTime] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const intervalRef = useRef(null);

  const startTimer = () => {
    setIsRunning(true);
    intervalRef.current = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
  };

  const pauseTimer = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
  };

  const resumeTimer = () => {
    startTimer();
  };

  const stopTimer = () => {
    clearInterval(intervalRef.current);
    setIsRunning(false);
    setTime(0);
  };

  const formatTime = (time) => {
    const minutes = Math.floor((time / 60000) % 60).toString().padStart(2, '0');
    const seconds = Math.floor((time / 1000) % 60).toString().padStart(2, '0');
    const milliseconds = Math.floor((time / 10) % 100).toString().padStart(2, '0');
    return `${minutes}:${seconds}.${milliseconds}`;
  };

  return (
    <div>
      <div>{formatTime(time)}</div>
      {!isRunning ? (
        <button onClick={startTimer}>Mulai</button>
      ) : (
        <div>
          <button onClick={pauseTimer}>Pause</button>
          <button onClick={stopTimer}>Berhenti</button>
        </div>
      )}
      {isRunning && <button onClick={resumeTimer}>Lanjut</button>}
    </div>
  );
};

export default Timer;
