import React, { useState, useEffect } from 'react';

const Timer = () => {
  const [time, setTime] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [isPaused, setIsPaused] = useState(false);

  useEffect(() => {
    let interval = null;

    if (isActive && !isPaused) {
      interval = setInterval(() => {
        setTime((prevTime) => prevTime - 10); // Mengurangi waktu dalam format milidetik (ms)
      }, 10);
    } else {
      clearInterval(interval);
    }

    return () => {
      clearInterval(interval);
    };
  }, [isActive, isPaused]);

  const handleStart = () => {
    setIsActive(true);
    setIsPaused(false);
    setTime(1800000);
  };

  const handlePause = () => {
    setIsPaused(true);
  };

  const handleResume = () => {
    setIsPaused(false);
  };

  const handleStop = () => {
    setIsActive(false);
    setIsPaused(false);
    setTime(1800000);
  };

  const formatTime = (time) => {
    const minutes = Math.floor((time / 1000 / 60) % 60).toString().padStart(2, '0');
    const seconds = Math.floor((time / 1000) % 60).toString().padStart(2, '0');
    const milliseconds = Math.floor((time % 1000) / 10).toString().padStart(2, '0');

    return `${minutes}:${seconds}.${milliseconds}`;
  };

  return (
    <div>
      <div>{formatTime(time)}</div>
      {!isActive && !isPaused && (
        <button onClick={handleStart} className="btn btn-primary btn-sm"><i className="fas fa-play"></i></button>
      )}
      {isActive && !isPaused && (
        <button onClick={handlePause}>Pause</button>
      )}
      {isPaused && (
        <div>
          <button onClick={handleResume}>Lanjut</button>
          <button onClick={handleStop}>Berhenti</button>
        </div>
      )}
    </div>
  );
};

export default Timer;
