import React from 'react';

class Tombol_vote extends React.Component {
  handleClick = () => {
    setTimeout(() => {
      alert('Ini adalah pesan alert!');
    }, 15000); // 15 detik

    // Cari semua jendela terbuka
    const windows = window.top.document.getElementsByClassName('your-page-class');

    // Iterasi melalui setiap jendela dan munculkan alert
    for (let i = 0; i < windows.length; i++) {
      const currentWindow = windows[i].contentWindow;
      currentWindow.postMessage('showAlert', '*');
    }
  }

  render() {
    return (
      <button onClick={this.handleClick}>Klik Saya!</button>
    );
  }
}

export default Tombol_vote;
