import React, { useState, useEffect } from 'react';

const Timer = () => {
  const [seconds, setSeconds] = useState(0);
  const [isActive, setIsActive] = useState(false);

  useEffect(() => {
    let interval = null;

    if (isActive) {
      interval = setInterval(() => {
        setSeconds((seconds) => seconds - 1);
      }, 1000);
    } else if (!isActive && seconds !== 0) {
      clearInterval(interval);
    }

    return () => clearInterval(interval);
  }, [isActive, seconds]);

  const handleStart = () => {
    setIsActive(true);
  };

  const handlePause = () => {
    setIsActive(false);
  };

  const handleStop = () => {
    setIsActive(false);
    setSeconds(0);
  };

  const handleReset = () => {
    setSeconds(0);
  };

  return (
    <div>
      <h1>Countdown Timer: {seconds}s</h1>
      <div>
        {!isActive && seconds === 0 && (
          <button onClick={handleStart}>Mulai</button>
        )}
        {isActive && (
          <button onClick={handlePause}>Jeda</button>
        )}
        {!isActive && seconds !== 0 && (
          <button onClick={handleStop}>Henti</button>
        )}
        {!isActive && seconds !== 0 && (
          <button onClick={handleReset}>Ulang</button>
        )}
      </div>
    </div>
  );
};

export default Timer;
