import React, { useState, useRef } from 'react';

const Timer = () => {
  const [time, setTime] = useState(0);
  const [isActive, setIsActive] = useState(false);
  const [isPaused, setIsPaused] = useState(false);
  const timerRef = useRef(null);

  const handleStart = () => {
    setIsActive(true);
    setIsPaused(false);
    timerRef.current = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
  };

  const handlePause = () => {
    clearInterval(timerRef.current);
    setIsPaused(true);
  };

  const handleResume = () => {
    setIsPaused(false);
    timerRef.current = setInterval(() => {
      setTime((prevTime) => prevTime + 10);
    }, 10);
  };

  const handleStop = () => {
    clearInterval(timerRef.current);
    setIsActive(false);
    setIsPaused(false);
    setTime(0);
  };

  const handleReset = () => {
    if (isActive || isPaused) return;
    setTime(0);
  };

  const formatTime = (milliseconds) => {
    const hours = Math.floor(milliseconds / 3600000);
    const minutes = Math.floor((milliseconds % 3600000) / 60000);
    const seconds = Math.floor((milliseconds % 60000) / 1000);
    const millisecondsFormatted = Math.floor((milliseconds % 1000) / 10);
    return `${hours}:${padTime(minutes)}:${padTime(seconds)}:${padTime(millisecondsFormatted)}`;
  };

  const padTime = (value) => {
    return value.toString().padStart(2, '0');
  };

  return (
    <div>
      <h1>Countdown Timer: {formatTime(time)}</h1>
      <div>
        {!isActive && !isPaused && (
          <button onClick={handleStart}>Mulai</button>
        )}
        {isActive && !isPaused && (
          <>
            <button onClick={handlePause}>Pause</button>
            <button onClick={handleStop}>Berhenti</button>
          </>
        )}
        {!isActive && isPaused && (
          <>
            <button onClick={handleResume}>Lanjut</button>
            <button onClick={handleStop}>Berhenti</button>
          </>
        )}
        {!isActive && !isPaused && time > 0 && (
          <button onClick={handleReset}>Reset</button>
        )}
      </div>
    </div>
  );
};

export default Timer;
